class LocalStorage {
    async setItem(key, value) {
      return new Promise((resolve, reject) => {
        try {
          localStorage.setItem(key, value);
          resolve();
        } catch (err) {
          reject(err);
        }
      });
    }

    async getItem(key) {
        return new Promise((resolve, reject) => {
            try {
            const value = localStorage.getItem(key);
            resolve(value);
            } catch (err) {
            reject(err);
            }
        });
        }


  }

