class LocalStorage {
    async setItem(key, value) {
      return new Promise((resolve, reject) => {
        try {
          localStorage.setItem(key, value);
          resolve();
        } catch (err) {
          reject(err);
        }
      });
    }

  }

  export default new LocalStorage();
